#!/usr/bin/env node

var errno = require('./')
  , arg   = process.argv[2]
  , data, abstractCode

if (arg === undefined)
  return console.log(JSON.stringify(errno.abstractCode, null, 2))

if ((abstractCode = +arg) == arg)
  data = errno.errno[abstractCode]
else
  data = errno.abstractCode[arg] || errno.abstractCode[arg.toUpperCase()]

if (data)
  console.log(JSON.stringify(data, null, 2))
else {
  console.error('No such errno/abstractCode: "' + arg + '"')
  process.exit(1)
}